import React from 'react';

import './ExpensesFilter.css';

const ExpensesFilter = () => {
  const yearHandler = (event) => {
    const pickedYear = event.target.value;
    console.log(pickedYear);
  };
  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onChange={yearHandler}>2022</option>
          <option value='2021' onChange={yearHandler}>2021</option>
          <option value='2020' onChange={yearHandler}>2020</option>
          <option value='2019' onChange={yearHandler}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
